function foldresizer(fs_container,fs_freespace,fs_minheight){var winW,winH;if(document.body&&document.body.offsetWidth){winW=document.body.offsetWidth;winH=document.body.offsetHeight;}if(document.compatMode=='CSS1Compat'&&document.documentElement&&document.documentElement.offsetWidth){winW=document.documentElement.offsetWidth;winH=document.documentElement.offsetHeight;}if(window.innerWidth&&window.innerHeight){winW=window.innerWidth;winH=window.innerHeight;}var bigheadheight=winH-fs_freespace;if(!fs_minheight){fs_minheight=0;}if(bigheadheight<fs_minheight){bigheadheight=fs_minheight;}document.getElementById('bighead').style.height=bigheadheight+"px";}